function listagem(lista){
    swap = true;
    while (swap){ 
        swap = false;
        for (let i = 0; i < lista.length; i++){

            if(lista[i] > lista[i+1]){
                maiornumber = lista[i]
                lista[i] = lista[i+1]
                lista[i+1] = maiornumber
                swap = true;
            }
        } 
    }  

    console.log(lista[lista.length-1])
}    

listagem(lista)


